import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchMovieDetails } from '../store/slices/movieSlice';
import { fetchMovieReviews, submitReview } from '../store/slices/reviewSlice';
import { addToWatchlist, removeFromWatchlist } from '../store/slices/watchlistSlice';
import LoadingSpinner from '../components/ui/LoadingSpinner';
import ErrorMessage from '../components/ui/ErrorMessage';

const MovieDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  
  const { currentMovie, loading: movieLoading, error: movieError } = useSelector(state => state.movies);
  const { reviews, loading: reviewsLoading } = useSelector(state => state.reviews);
  const { isAuthenticated, user } = useSelector(state => state.auth);
  const { movies: watchlistMovies } = useSelector(state => state.watchlist);
  
  const [userReview, setUserReview] = useState({ rating: 5, title: '', content: '', spoilers: false });
  const [showReviewForm, setShowReviewForm] = useState(false);

  const isInWatchlist = watchlistMovies.some(movie => movie._id === id);

  useEffect(() => {
    if (id) {
      dispatch(fetchMovieDetails(id));
      dispatch(fetchMovieReviews({ movieId: id }));
    }
  }, [dispatch, id]);

  // Debug logging
  useEffect(() => {
    console.log('Auth state:', { isAuthenticated, user });
    console.log('Movie data:', currentMovie);
    console.log('Poster URL:', currentMovie?.posterUrl);
  }, [isAuthenticated, user, currentMovie]);

  const handleWatchlistToggle = () => {
    console.log('Watchlist toggle clicked. Auth state:', { isAuthenticated, user: user?._id });
    
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    // Add a small delay to ensure user state is loaded
    if (!user?._id) {
      console.log('User ID not found, waiting for auth state...');
      setTimeout(() => {
        if (user?._id) {
          if (isInWatchlist) {
            dispatch(removeFromWatchlist({ userId: user._id, movieId: id }));
          } else {
            dispatch(addToWatchlist({ userId: user._id, movieId: id }));
          }
        } else {
          navigate('/login');
        }
      }, 100);
      return;
    }

    if (isInWatchlist) {
      dispatch(removeFromWatchlist({ userId: user._id, movieId: id }));
    } else {
      dispatch(addToWatchlist({ userId: user._id, movieId: id }));
    }
  };

  const handleSubmitReview = (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    dispatch(submitReview({
      movieId: id,
      reviewData: userReview
    })).then((result) => {
      if (result.type === 'reviews/submitReview/fulfilled') {
        // Reset form and close it
        setUserReview({ rating: 5, title: '', content: '', spoilers: false });
        setShowReviewForm(false);
        
        // Refresh both reviews and movie details to update statistics
        dispatch(fetchMovieReviews({ movieId: id }));
        dispatch(fetchMovieDetails(id));
      }
    });
  };

  if (movieLoading && !currentMovie) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center items-center min-h-64">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  if (movieError) {
    return (
      <div className="container mx-auto px-4 py-8">
        <ErrorMessage message={movieError} />
      </div>
    );
  }

  if (!currentMovie) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900">Movie not found</h1>
          <button
            onClick={() => navigate('/movies')}
            className="mt-4 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
          >
            Browse Movies
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Movie Header */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden mb-8">
        <div className="md:flex">
          {/* Movie Poster */}
          <div className="md:w-1/3 lg:w-1/4 relative">
            {currentMovie.posterUrl ? (
              <img
                src={currentMovie.posterUrl}
                alt={currentMovie.title}
                className="w-full h-64 md:h-full object-cover"
                onError={(e) => {
                  console.log('Poster failed to load:', currentMovie.posterUrl);
                  e.target.style.display = 'none';
                  e.target.nextElementSibling.style.display = 'flex';
                }}
                onLoad={() => {
                  console.log('Poster loaded successfully:', currentMovie.posterUrl);
                }}
              />
            ) : null}
            <div
              className="w-full h-64 md:h-full bg-gray-200 flex items-center justify-center text-gray-500 border absolute top-0"
              style={currentMovie.posterUrl ? { display: 'none' } : { display: 'flex' }}
            >
              <div className="text-center">
                <svg className="w-16 h-16 mx-auto mb-2 text-gray-400" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M4 3h16l-2 4H6L4 3zm0 6h16v2H4V9zm2 4h12v6H6v-6z"/>
                </svg>
                <div className="text-sm font-medium">No Poster Available</div>
              </div>
            </div>
          </div>
          
          {/* Movie Info */}
          <div className="md:w-2/3 lg:w-3/4 p-6">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">{currentMovie.title}</h1>
                <p className="text-gray-600 mb-2">{currentMovie.year} • {currentMovie.runtime} min</p>
                <div className="flex items-center space-x-4 mb-4">
                  <div className="flex items-center">
                    <span className="text-yellow-400 text-lg">★</span>
                    <span className="ml-1 font-medium">{currentMovie.averageRating?.toFixed(1) || 'N/A'}</span>
                    <span className="text-gray-500 ml-1">({currentMovie.totalReviews || 0} reviews)</span>
                  </div>
                </div>
              </div>
              
              <button
                onClick={handleWatchlistToggle}
                className={`px-4 py-2 rounded-md font-medium ${
                  isInWatchlist
                    ? 'bg-red-100 text-red-800 hover:bg-red-200'
                    : 'bg-blue-100 text-blue-800 hover:bg-blue-200'
                }`}
              >
                {isInWatchlist ? 'Remove from Watchlist' : 'Add to Watchlist'}
              </button>
            </div>
            
            {/* Genres */}
            <div className="flex flex-wrap gap-2 mb-4">
              {currentMovie.genres?.map(genre => (
                <span
                  key={genre}
                  className="px-2 py-1 bg-gray-100 text-gray-800 text-sm rounded-full"
                >
                  {genre}
                </span>
              ))}
            </div>
            
            {/* Synopsis */}
            <div>
              <h3 className="text-lg font-semibold mb-2">Synopsis</h3>
              <p className="text-gray-700 leading-relaxed">
                {currentMovie.synopsis || 'No synopsis available.'}
              </p>
            </div>
            
            {/* Cast & Crew */}
            {(currentMovie.director || currentMovie.cast?.length > 0) && (
              <div className="mt-4">
                {currentMovie.director && (
                  <p className="text-sm text-gray-600 mb-1">
                    <span className="font-medium">Director:</span> {currentMovie.director}
                  </p>
                )}
                {currentMovie.cast?.length > 0 && (
                  <p className="text-sm text-gray-600">
                    <span className="font-medium">Cast:</span> {currentMovie.cast.join(', ')}
                  </p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Reviews Section */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900">Reviews</h2>
          {isAuthenticated && (
            <button
              onClick={() => setShowReviewForm(!showReviewForm)}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Write Review
            </button>
          )}
        </div>

        {/* Review Form */}
        {showReviewForm && (
          <form onSubmit={handleSubmitReview} className="mb-6 p-4 bg-gray-50 rounded-lg">
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">Rating</label>
              <div className="flex space-x-2">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    type="button"
                    onClick={() => setUserReview({ ...userReview, rating: star })}
                    className={`text-2xl ${
                      star <= userReview.rating ? 'text-yellow-400' : 'text-gray-300'
                    } hover:text-yellow-400`}
                  >
                    ★
                  </button>
                ))}
              </div>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">Review Title</label>
              <input
                type="text"
                value={userReview.title}
                onChange={(e) => setUserReview({ ...userReview, title: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Give your review a title (3-100 characters)"
                minLength={3}
                maxLength={100}
                required
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">Your Review</label>
              <textarea
                value={userReview.content}
                onChange={(e) => setUserReview({ ...userReview, content: e.target.value })}
                rows={4}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="Share your thoughts about this movie (10-2000 characters)"
                minLength={10}
                maxLength={2000}
                required
              />
            </div>
            
            <div className="mb-4">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={userReview.spoilers}
                  onChange={(e) => setUserReview({ ...userReview, spoilers: e.target.checked })}
                  className="mr-2"
                />
                <span className="text-sm text-gray-700">This review contains spoilers</span>
              </label>
            </div>
            
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
              >
                Submit Review
              </button>
              <button
                type="button"
                onClick={() => setShowReviewForm(false)}
                className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </form>
        )}

        {/* Reviews List */}
        {reviewsLoading ? (
          <div className="flex justify-center py-8">
            <LoadingSpinner />
          </div>
        ) : reviews && reviews.length > 0 ? (
          <div className="space-y-4">
            {reviews.map(review => (
              <div key={review._id} className="border-b border-gray-200 pb-4 last:border-b-0">
                <div className="flex justify-between items-start mb-2">
                  <div>
                    <h4 className="font-medium text-gray-900">{review.user?.username || 'Anonymous'}</h4>
                    <div className="flex items-center space-x-2">
                      <div className="flex text-yellow-400">
                        {[...Array(5)].map((_, i) => (
                          <span key={i} className={i < review.rating ? 'text-yellow-400' : 'text-gray-300'}>
                            ★
                          </span>
                        ))}
                      </div>
                      <span className="text-sm text-gray-500">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
                <p className="text-gray-700 leading-relaxed">{review.comment}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500 py-8">
            No reviews yet. Be the first to review this movie!
          </p>
        )}
      </div>
    </div>
  );
};

export default MovieDetailPage;
