import React from 'react';
import { Link } from 'react-router-dom';
import { FiStar, FiCalendar, FiClock } from 'react-icons/fi';
import { format } from 'date-fns';

const MovieCard = ({ movie }) => {
  const {
    _id,
    title,
    posterUrl,
    releaseDate,
    averageRating,
    totalRatings,
    runtime,
    genres = []
  } = movie;

  const formatRuntime = (minutes) => {
    if (!minutes) return '';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getRatingColor = (rating) => {
    if (rating >= 4.5) return 'text-green-600';
    if (rating >= 3.5) return 'text-yellow-600';
    if (rating >= 2.5) return 'text-orange-600';
    return 'text-red-600';
  };

  return (
    <Link to={`/movies/${_id}`} className="movie-card group">
      <div className="relative">
        {/* Movie Poster */}
        <img
          src={posterUrl || '/placeholder-movie.jpg'}
          alt={title}
          className="movie-poster"
          loading="lazy"
          style={{
            width: '100%',
            height: '200px',
            objectFit: 'cover',
            borderRadius: '8px'
          }}
        />
        
        {/* Overlay with quick actions */}
        <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
          <div className="text-white text-center">
            <div className="text-sm font-medium mb-2">Quick View</div>
            <div className="flex items-center justify-center space-x-4 text-xs">
              {runtime && (
                <div className="flex items-center">
                  <FiClock className="h-3 w-3 mr-1" />
                  {formatRuntime(runtime)}
                </div>
              )}
              {averageRating > 0 && (
                <div className="flex items-center">
                  <FiStar className="h-3 w-3 mr-1 text-yellow-400" />
                  {averageRating.toFixed(1)}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Movie Info */}
      <div className="p-4">
        <h3 className="font-semibold text-gray-900 line-clamp-2 mb-2 group-hover:text-primary-600 transition-colors">
          {title}
        </h3>

        {/* Rating */}
        {averageRating > 0 && (
          <div className="flex items-center mb-2">
            <div className="flex items-center">
              <FiStar className="h-4 w-4 text-yellow-400 mr-1" />
              <span className={`text-sm font-medium ${getRatingColor(averageRating)}`}>
                {averageRating.toFixed(1)}
              </span>
            </div>
            <span className="text-xs text-gray-500 ml-2">
              ({totalRatings} {totalRatings === 1 ? 'review' : 'reviews'})
            </span>
          </div>
        )}

        {/* Release Date */}
        {releaseDate && (
          <div className="flex items-center text-xs text-gray-500 mb-2">
            <FiCalendar className="h-3 w-3 mr-1" />
            {format(new Date(releaseDate), 'MMM yyyy')}
          </div>
        )}

        {/* Genres */}
        {genres.length > 0 && (
          <div className="flex flex-wrap gap-1">
            {genres.slice(0, 2).map((genre, index) => (
              <span
                key={index}
                className="tag tag-secondary text-xs"
              >
                {genre}
              </span>
            ))}
            {genres.length > 2 && (
              <span className="text-xs text-gray-500">
                +{genres.length - 2}
              </span>
            )}
          </div>
        )}
      </div>
    </Link>
  );
};

export default MovieCard;
